-- Add missing columns to inventory_items table
ALTER TABLE inventory_items 
ADD COLUMN IF NOT EXISTS inventory_item_id INTEGER NOT NULL DEFAULT 0,
ADD COLUMN IF NOT EXISTS rarity_tier VARCHAR(50) NOT NULL DEFAULT 'Common',
ADD COLUMN IF NOT EXISTS is_capture_type BOOLEAN NOT NULL DEFAULT false,
ADD COLUMN IF NOT EXISTS item_type VARCHAR(50) NOT NULL DEFAULT 'consumable',
ADD COLUMN IF NOT EXISTS equipment_slot VARCHAR(50);

-- Create unique index on inventory_item_id 
CREATE UNIQUE INDEX IF NOT EXISTS idx_inventory_items_inventory_item_id ON inventory_items(inventory_item_id);

